import type { GlossaryTerm } from '../types';

function createTerm(id: number, data: string[]): GlossaryTerm {
  const categories: string[] = [];
  if (data[6] === '〇') categories.push('一般');
  if (data[7] === '〇') categories.push('ALJ専門');
  if (data[8] === '〇') categories.push('トヨタ用語');
  if (data[9] === '〇') categories.push('その他');
  if (data[10] === '〇') categories.push('樹脂 成型');
  if (data[11] === '〇') categories.push('樹脂金型');
  if (data[12] === '〇') categories.push('設計専門');

  return {
    id,
    term: data[1],
    reading: data[2].replace(/\n/g, ''),
    alias: data[3],
    english: data[4],
    meaning: data[5],
    categories,
    notes: data[13],
    author: data[14],
    dateAdded: data[15],
    updater: data[16] || undefined,
    dateUpdated: data[17] || undefined,
    imageUrl: data[18] || undefined,
  };
}

